dp <- function(q) {
  return(-0.6*q + 8)
}
# 供給曲線
sp <- function(q) {
  return(1*q)
}

# 作図
png('sample.png', width=500, height=500)
par(pty='s') # デフォルトは'm'
plot(sp, xlim=c(0, 10), ylim=c(0, 10), xlab='Q', ylab='P')
par(new=t)
